import React from 'react';

const sampleTable = [
  { id:1, title: 'The Pragmatic Programmer', author: 'Andy Hunt' },
  { id:2, title: 'Clean Code', author: 'Robert C. Martin' },
  { id:3, title: 'You Dont Know JS', author: 'Kyle Simpson' }
];

export default function Dashboard(){
  const user = JSON.parse(localStorage.getItem('user') || null);
  const logout = () => { localStorage.removeItem('token'); localStorage.removeItem('user'); window.location = '/login'; };

  return (
    <div className="card">
      <div className="row header">
        <h2>Dashboard</h2>
        <div>
          <strong>{user?.name}</strong>
          <button className="btn small" onClick={logout}>Logout</button>
        </div>
      </div>

      <p>Protected static table (only visible after login)</p>
      <table className="table">
        <thead><tr><th>ID</th><th>Title</th><th>Author</th></tr></thead>
        <tbody>
          {sampleTable.map(r => (
            <tr key={r.id}><td>{r.id}</td><td>{r.title}</td><td>{r.author}</td></tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
